"use strict";

let fa = require("site/shared.manifest").feArray;
let web = require("site/shared.manifest").web;
require("./shared.main");
let client = require("site/client");

let lit = client.lit;
let ui = client.ui;
ui.setRem(0.03);
let stack;
let nodeVersionText;
ui.root.add(stack = new ui.Stack(
    {scale: 0},
    new ui.Text({fontSize: 1.6}, lit.nodeVersionTitle),
    nodeVersionText = new ui.Text()
));
setImmediate(() => {
    stack.setTransition(800).setScale(1);
});
web.jsonGet("/node/version").then(response => {
    nodeVersionText.setSource(response.body);
});
